'use strict';

function Context(response, data, object) {
    if (!object) {
        return object;
    }

    object[Context.RESPONSE]  = response;
    object[Context.DATA]      = data;

    return object;
}

Context.URL  = Symbol('Context.URL');
Context.DATA = Symbol('Context.DATA');
Context.TYPE = Symbol('Context.TYPE');

module.exports = Context;
